function ImagePopup(props) {
  return (
    <div
      className={`popup popup-img ${
        props.card.link.length > 0 && 'popup_opened'
      }`}
    >
      <div class="popup__container-img">
        <img
          src={`${props.card.link}`}
          alt={`картинка ${props.card.name}`}
          class="popup__img"
        />
        <span class="popup__img-title">{props.card.name}</span>
        <button
          class="popup__close-button popup__close-button_img hover"
          type="button"
          onClick={props.onClose}
        ></button>
      </div>
    </div>
  );
}
export default ImagePopup;
