// import avatar from '../images/Avatar.png'
import plus from '../images/plus.svg';
import { useState, useEffect } from 'react';
import { api } from '../utils/api.js';
import Card from './Card.js';

function Main(props) {
  const [userName, setUserName] = useState();
  const [userDescription, setUserDescription] = useState();
  const [userAvatar, setUserAvatar] = useState();
  const [cards, setCards] = useState([]);

  useEffect(() => {
    api
      .loadUser()
      .then((data) => {
        setUserName(data.name);
        setUserDescription(data.about);
        setUserAvatar(data.avatar);
      })
      .catch((err) => console.log(err.status));

    api
      .getInitialCards()
      .then((data) => {
        setCards(data);
      })
      .catch((err) => console.log(err));
  }, []);

  return (
    <main>
      <section className="profile container">
        <a
          href="# "
          className="profile__container"
          target="_self"
          onClick={props.onEditAvatar}
        >
          <img
            src={userAvatar}
            alt="Фото профиля"
            className="profile__image profile__image-btn"
          />
        </a>

        <div className="profile__credentials">
          <div className="profile__title-wrapper">
            <h1 className="profile__credentials-title">{userName}</h1>
            <button
              className="profile__edit-button hover"
              onClick={props.onEditProfile}
            ></button>
          </div>
          <p className="profile__credentials-subtitle">{userDescription}</p>
        </div>
        <button
          className="profile__btn hover"
          type="button"
          onClick={props.onAddPlace}
        >
          <img
            src={plus}
            alt="Кнопка плюс"
            className="profile__btn-content"
            onClick={props.onAddPlace}
          />
        </button>
      </section>
      <section className="gallery container">
        {cards.map((item) => (
          <Card key={item._id} card={item} onCardClick={props.onCardClick} />
        ))}
      </section>
    </main>
  );
}
export default Main;
