import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import {useState, useEffect} from 'react';

import {api} from "../utils/api";
import {CurrentUserContext} from "../contexts/CurrentUserContext";

function App() {
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
    const [selectedCard, setSelectedCard] = useState({name: '', link: ''});


    const [currentUser, setCurrentUser] = useState();

    useEffect(() => {
        api
            .then((data) => {
                setCurrentUser(data.name);
                setCurrentUser(data.about);
                setCurrentUser(data.avatar);
            })
            .catch((err) => console.log(err.status));
    },[])

    function handleEditAvatarClick() {
        setIsEditAvatarPopupOpen(true);
    }

    function handleEditProfileClick() {
        setIsEditProfilePopupOpen(true);
    }

    function handleAddPlaceClick() {
        setIsAddPlacePopupOpen(true);
    }

    function closeAllPopups() {
        setIsEditProfilePopupOpen(false);
        setIsAddPlacePopupOpen(false);
        setIsEditAvatarPopupOpen(false);
        setSelectedCard({name: '', link: ''});
    }

    function handleCardClick(cardData) {
        setSelectedCard(cardData);
    }

    return (
        <div className="page">
            <Header/>
            <CurrentUserContext.Provider
                value={currentUser}
            >
                <Main
                    onEditProfile={handleEditProfileClick}
                    onAddPlace={handleAddPlaceClick}
                    onEditAvatar={handleEditAvatarClick}
                    onCardClick={handleCardClick}
                />
            </CurrentUserContext.Provider>
            <Footer/>
            <PopupWithForm
                name="popup_add"
                title="Новое место"
                button="Создать"
                isOpen={isAddPlacePopupOpen}
                onClose={closeAllPopups}
            >
                <input
                    type="text"
                    name="name"
                    defaultValue=""
                    placeholder="Название"
                    id="place"
                    className="popup__field popup__input popup__input_place"
                    required
                    minLength="2"
                    maxLength="30"
                />
                <span id="place-error" className="popup__error popup__error_visible"></span>
                <input
                    type="url"
                    name="link"
                    defaultValue=""
                    placeholder="Ссылка на картинку"
                    id="link"
                    className="popup__field popup__input popup__input_link"
                    required
                />
                <span id="link-error" className="popup__error popup__error_visible"></span>
            </PopupWithForm>

            <PopupWithForm
                name="popup_delete"
                title="Вы уверены?"
                button="Да"
            />

            <PopupWithForm
                name="popup_update-avatar"
                title="Обновить аватар"
                button="Сохранить"
                isOpen={isEditAvatarPopupOpen}
                onClose={closeAllPopups}
            >
                <label className="popup__label">
                    <input
                        id="avatar__input"
                        type="url"
                        name="avatar"
                        defaultValue=""
                        placeholder="Ссылка на картинку"
                        className="popup__field popup__field_add popup__image-link popup__input"
                        required
                    />
                    <span id="avatar__input-error"></span>
                </label>
            </PopupWithForm>

            <PopupWithForm
                name="popup_edit"
                title="Редактировать профиль"
                button="Сохранить"
                isOpen={isEditProfilePopupOpen}
                onClose={closeAllPopups}
            >
                <input
                    type="text"
                    name="name"
                    defaultValue=""
                    id="nickname"
                    placeholder="Имя"
                    className="popup__field popup__input"
                    required
                    minLength="2"
                    maxLength="40"
                />
                <span id="nickname-error" className="popup__error popup__error_visible"></span>
                <input
                    type="text"
                    name="about"
                    defaultValue=""
                    id="info"
                    placeholder="Вид деятельности"
                    className="popup__field popup__input"
                    required
                    minLength="2"
                    maxLength="200"
                />
                <span id="info-error" className="popup__error popup__error_visible"></span>
            </PopupWithForm>

            <ImagePopup
                card={selectedCard}
                onClose={closeAllPopups}
            />
        </div>
    );
}

export default App;
