// import avatar from '../images/Avatar.png'
import plus from '../images/plus.svg'
import { useState, useEffect } from 'react';
import { api } from '../utils/api.js'


function Main(props) {

  const [userName, setUserName] = useState();
  const [userDescription, setUserDescription] = useState();
  const [userAvatar, setUserAvatar] = useState();

  useEffect(() => {
    api.loadUser()
        .then(data => {
            setUserName(data.name);
            setUserDescription(data.about);
            setUserAvatar(data.avatar);
        })
        .catch(err => console.log(err.status));
      }, []);




  return (
    <main>
    <section class="profile container">
        <a href="#" class="profile__container" target="_self"
                  onClick={props.onEditAvatar}
                  >        
          <img
          src={userAvatar}
          alt="Фото профиля"
          class="profile__image profile__image-btn"
          />
        </a>

        <div class="profile__credentials">
          <div class="profile__title-wrapper">
            <h1 class="profile__credentials-title">{userName}</h1>
            <button class="profile__edit-button hover"
            onClick={props.onEditProfile}
            ></button>
          </div>
          <p class="profile__credentials-subtitle">{userDescription}</p>
        </div>
        <button class="profile__btn hover" type="button"
        onClick={props.onAddPlace}
        >
          <img
            src={plus}
            alt="Кнопка плюс"
            class="profile__btn-content"
            // onClick={handleClick}
          />
        </button>
      </section>
      <section class="gallery container"></section>
    </main>
  )
}
export default Main ;
