import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import PopupWithForm from "./PopupWithForm";
import ImagePopup from "./ImagePopup";
import EditProfilePopup from "./EditProfilePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import { useEffect, useState } from "react";

import { api } from "../utils/api";
import { CurrentUserContext } from "../contexts/CurrentUserContext";

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({ name: "", link: "" });
  const [cards, setCards] = useState([]);
  const [currentUser, setCurrentUser] = useState('');

  useEffect(() => {
    api
        .getInitialCards()
        .then((data) => {
          setCards(data);
        })
    api
        .loadUser()
        .then((data) => {
          setCurrentUser(data);
        })
  }, []);

  function handleCardLike(card) {
    const isLiked = card.likes.some((likes) => likes._id === currentUser._id);
    api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
      setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
    });
  }

  function handleCardDelete(removedCard) {
    api.removeCard(removedCard._id).then(() => {
      const newArr = cards.filter(card => card._id !== removedCard._id);
      setCards(newArr);
    })
  }

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard({ name: "", link: "" });
  }

  function handleCardClick(cardData) {
    selectedCard(cardData)
  }

  function handleUpdateUser(formData) {
    api
      .updateUserInfo(formData)
      .then((formData) => {
        setCurrentUser(formData);
      })
    closeAllPopups();
  }
  function handleUpdateAvatar(formData) {
    api
      .updateAvatar(formData)
      .then((formData) => {
        setCurrentUser(formData);
      })
    closeAllPopups();
  }

  return (
    <div className="page">
      <Header />
      <CurrentUserContext.Provider value={currentUser}>
        <Main
          cards={cards}
          onCardLike={handleCardLike}
          onCardDelete={handleCardDelete}
          onEditProfile={handleEditProfileClick}
          onAddPlace={handleAddPlaceClick}
          onEditAvatar={handleEditAvatarClick}
          onCardClick={handleCardClick}
        />
        <Footer />
        <EditProfilePopup
            isOpen={isEditProfilePopupOpen}
            onClose={closeAllPopups}
            onUpdateUser={handleUpdateUser}
        />
        <EditAvatarPopup
            isOpen={isEditAvatarPopupOpen}
            onClose={closeAllPopups}
            onUpdateAvatar={handleUpdateAvatar}
        />
      </CurrentUserContext.Provider>

      <PopupWithForm
        name="popup_add"
        title="Новое место"
        button="Создать"
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
      >
        <input
          type="text"
          name="name"
          defaultValue=""
          placeholder="Название"
          id="place"
          className="popup__field popup__input popup__input_place"
          required
          minLength="2"
          maxLength="30"
        />
        <span
    id="place-error"
    className="popup__error popup__error_visible"
    />
        <input
          type="url"
          name="link"
          defaultValue=""
          placeholder="Ссылка на картинку"
          id="link"
          className="popup__field popup__input popup__input_link"
          required
        />
        <span
    id="link-error"
    className="popup__error popup__error_visible"
    />
      </PopupWithForm>

      <PopupWithForm name="popup_delete" title="Вы уверены?" button="Да" />


      <ImagePopup card={selectedCard} onClose={closeAllPopups} />
    </div>
  );
}

export default App;
