import React from 'react';
import {CurrentUserContext} from "../contexts/CurrentUserContext";



function Card(props) {
    function handleClick() {
        props.onCardClick({name: props.card.name, link: props.card.link});
    }
    const isOwn = card.owner._id === currentUser._id;
    const cardDeleteButtonClassName = (
        `card__delete-button ${isOwn ? 'card__delete-button_visible' : 'card__delete-button_hidden'}`
    );

    return (
        <div className="card">
            <img
                src={props.card.link}
                alt={`картинка ${props.card.name}`}
                className="card__image"
                onClick={handleClick}
            />
            <button className="card__image-button hover"></button>
            <div className="card__description">
                <h2 className="card__description-title">{props.card.name}</h2>
                <div className="card__like-container">
                    <button className="card__like-button hover" type="button">{isOwn}</button>
                    <p className="card__like">{props.card.likes.length}</p>
                </div>
            </div>
        </div>
    );
}

export default Card;
