// import avatar from '../images/Avatar.png'
import React from 'react';
import plus from '../images/plus.svg';
import {useState, useEffect} from 'react';
import {api} from '../utils/api.js';
import Card from './Card.js';
import {CurrentUserContext} from "../contexts/CurrentUserContext";

function Main(props) {
    // const [userName, setUserName] = useState();
    // const [userDescription, setUserDescription] = useState();
    // const [userAvatar, setUserAvatar] = useState();
    const [cards, setCards] = useState([]);

    const currentUser = React.useContext(CurrentUserContext);

    function handleCardLike(card) {

       const isLiked = card.likes.some((likes) => likes._id === currentUser._id);


        api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
            setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        });
    }

    function handleCardDelete(removedCard) {
        api.removeCard(removedCard._id).then(() => {
            const newArr = cards.filter(card => card._id !== removedCard._id);
            setCards(newArr);
        })
    }

    useEffect(() => {
        // api
        //     .loadUser()
        //     .then((data) => {
        //         setUserName(data.name);
        //         setUserDescription(data.about);
        //         setUserAvatar(data.avatar);
        //     })
        //     .catch((err) => console.log(err.status));

        api
            .getInitialCards()
            .then((data) => {
                setCards(data);
            })
            .catch((err) => console.log(err));
    }, []);

    return (
        <main>
            <section className="profile container">
                <a
                    href="# "
                    className="profile__container"
                    target="_self"
                    onClick={props.onEditAvatar}
                >
                    <img
                        src={currentUser.avatar}
                        alt="Фото профиля"
                        className="profile__image profile__image-btn"
                    />
                </a>

                <div className="profile__credentials">
                    <div className="profile__title-wrapper">
                        <h1 className="profile__credentials-title">{currentUser.name}</h1>
                        <button
                            className="profile__edit-button hover"
                            onClick={props.onEditProfile}
                        />
                    </div>
                    <p className="profile__credentials-subtitle">{currentUser.about}</p>
                </div>
                <button
                    className="profile__btn hover"
                    type="button"
                    onClick={props.onAddPlace}
                >
                    <img
                        src={plus}
                        alt="Кнопка плюс"
                        className="profile__btn-content"
                        onClick={props.onAddPlace}
                    />
                </button>
            </section>
            <section className="gallery container">
                {cards.map((item) => (
                    <Card key={item._id} card={item} onCardClick={props.onCardClick} onCardDelete={handleCardDelete} onCardLike={handleCardLike}/>
                ))}
            </section>
        </main>
    );
}

export default Main;
