import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ ImagePopup';
import { useState, useEffect } from 'react';

function App() {
  const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
  const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);
  const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
  const [selectedCard, setSelectedCard] = useState({ name: '', link: '' });

  function handleEditAvatarClick() {
    setIsEditAvatarPopupOpen(true);
  }

  function handleEditProfileClick() {
    setIsEditProfilePopupOpen(true);
  }

  function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true);
  }

  function closeAllPopups() {
    setIsEditProfilePopupOpen(false);
    setIsAddPlacePopupOpen(false);
    setIsEditAvatarPopupOpen(false);
    setSelectedCard({ name: '', link: '' });
  }

  function handleCardClick(cardData) {
    setSelectedCard(cardData);
  }

  return (
    <body class="page">
      <Header />
      <Main
        onEditProfile={handleEditProfileClick}
        onAddPlace={handleAddPlaceClick}
        onEditAvatar={handleEditAvatarClick}
        onCardClick={handleCardClick}
      />
      <Footer />
      <PopupWithForm
        name="popup_add"
        title="Новое место"
        button="Создать"
        isOpen={isAddPlacePopupOpen}
        onClose={closeAllPopups}
      >
        <input
          type="text"
          name="name"
          value=""
          placeholder="Название"
          id="place"
          class="popup__field popup__input popup__input_place"
          required
          minlength="2"
          maxlength="30"
        />
        <span id="place-error" class="popup__error popup__error_visible"></span>
        <input
          type="url"
          name="link"
          value=""
          placeholder="Ссылка на картинку"
          id="link"
          class="popup__field popup__input popup__input_link"
          required
        />
        <span id="link-error" class="popup__error popup__error_visible"></span>
      </PopupWithForm>

      <PopupWithForm title="Вы уверены?" button="Да"></PopupWithForm>

      <PopupWithForm
        title="Обновить аватар"
        button="Сохранить"
        isOpen={isEditAvatarPopupOpen}
        onClose={closeAllPopups}
      >
        <label class="popup__label">
          <input
            id="avatar__input"
            type="url"
            name="avatar"
            value=""
            placeholder="Ссылка на картинку"
            class="popup__field popup__field_add popup__image-link popup__input"
            required
          />
          <span id="avatar__input-error"></span>
        </label>
      </PopupWithForm>

      <PopupWithForm
        title="Редактировать профиль"
        button="Сохранить"
        isOpen={isEditProfilePopupOpen}
        onClose={closeAllPopups}
      >
        <input
          type="text"
          name="name"
          value=""
          id="nickname"
          placeholder="Имя"
          class="popup__field popup__input"
          required
          minlength="2"
          maxlength="40"
        />
        <span
          id="nickname-error"
          class="popup__error popup__error_visible"
        ></span>
        <input
          type="text"
          name="about"
          value=""
          id="info"
          placeholder="Вид деятельности"
          class="popup__field popup__input"
          required
          minlength="2"
          maxlength="200"
        />
        <span id="info-error" class="popup__error popup__error_visible"></span>
      </PopupWithForm>

      <ImagePopup card={selectedCard} onClose={closeAllPopups} />
    </body>
  );
}

export default App;
